files.content:
body {
  margin: 0;
  padding: 0;
  font-family: 'Segoe UI', sans-serif;
  background: linear-gradient(135deg, #ffe7d9, #ffd1f0);
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
}

.container {
  background: #ffffffcc;
  backdrop-filter: blur(10px);
  padding: 40px 50px;
  border-radius: 20px;
  box-shadow: 0 10px 40px rgba(0,0,0,0.15);
  text-align: center;
  max-width: 600px;
}

h1 {
  font-size: 2.5rem;
  margin-bottom: 20px;
  color: #c05ba8;
  font-weight: 700;
  text-shadow: 1px 1px 2px #ffffff;
}

.mensagem {
  font-size: 1.2rem;
  line-height: 1.6;
  color: #444;
  margin-bottom: 30px;
}

.assinatura {
  font-size: 1rem;
  color: #666;
  font-style: italic;
}
