conteudo:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
}

body {
  background: #f3f4f6;
  color: #111827;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
}

.page {
  width: 100%;
  max-width: 960px;
}

.header {
  margin-bottom: 24px;
  text-align: center;
}

.header h1 {
  font-size: 1.8rem;
  font-weight: 700;
  color: #111827;
}

.subtitle {
  margin-top: 4px;
  font-size: 0.95rem;
  color: #6b7280;
}

.grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
}

.card {
  background: #ffffff;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 10px 20px rgba(15, 23, 42, 0.08);
  border: 1px solid #e5e7eb;
}

.card h2 {
  font-size: 1.2rem;
  margin-bottom: 4px;
  color: #111827;
}

.card-desc {
  font-size: 0.9rem;
  color: #6b7280;
  margin-bottom: 16px;
}

.field {
  display: flex;
  flex-direction: column;
  margin-bottom: 12px;
}

.field label {
  font-size: 0.85rem;
  margin-bottom: 4px;
  color: #374151;
}

.field input {
  padding: 8px 10px;
  border-radius: 8px;
  border: 1px solid #d1d5db;
  font-size: 0.95rem;
  outline: none;
  transition: border-color 0.15s ease, box-shadow 0.15s ease, background-color 0.15s ease;
  background-color: #f9fafb;
}

.field input:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.2);
  background-color: #ffffff;
}

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 8px 14px;
  border-radius: 999px;
  border: none;
  cursor: pointer;
  font-size: 0.95rem;
  font-weight: 600;
  margin-top: 6px;
  transition: transform 0.1s ease, box-shadow 0.1s ease, background-color 0.15s ease;
}

.btn.primary {
  background: #2563eb;
  color: #ffffff;
  box-shadow: 0 10px 15px rgba(37, 99, 235, 0.25);
}

.btn.primary:hover {
  background: #1d4ed8;
  transform: translateY(-1px);
  box-shadow: 0 16px 25px rgba(37, 99, 235, 0.3);
}

.btn.secondary {
  background: #6b7280;
  color: #ffffff;
  box-shadow: 0 10px 15px rgba(55, 65, 81, 0.25);
}

.btn.secondary:hover {
  background: #4b5563;
  transform: translateY(-1px);
  box-shadow: 0 16px 25px rgba(55, 65, 81, 0.3);
}

.msg {
  margin-top: 10px;
  min-height: 18px;
  font-size: 0.85rem;
}

.msg.success {
  color: #065f46;
}

.msg.error {
  color: #b91c1c;
}

@media (max-width: 640px) {
  body {
    padding: 16px;
  }

  .card {
    padding: 16px;
  }

  .header h1 {
    font-size: 1.4rem;
  }
}
