/* ======================================================================
 * style.css • PBQE-C V2 Clean Light – Base global
 * ---------------------------------------------------------------------- */
:root {
  --bg-body: #f3f4f6;
  --bg-card: #ffffff;
  --border-soft: #e5e7eb;
  --text-main: #111827;
  --text-muted: #6b7280;
  --accent: #f97316;
  --accent-soft: rgba(249, 115, 22, 0.08);
  --danger: #dc2626;
  --success: #16a34a;
  --warn: #d97706;
  --radius-lg: 16px;
  --shadow-soft: 0 18px 45px rgba(15, 23, 42, 0.12);
  --transition-fast: 150ms ease-out;
}

*,
*::before,
*::after {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: radial-gradient(circle at top, #fef3c7 0, #f3f4f6 42%, #e5e7eb 100%);
  color: var(--text-main);
}

/* Layouts base */
.page {
  min-height: 100vh;
}

.page-auth,
.page-welcome {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
}

.page-dashboard {
  padding: 16px;
}

/* Tipografia */
h1, h2, h3, h4 {
  margin: 0;
  font-weight: 600;
}

/* Botões */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
  border-radius: 999px;
  border: 1px solid transparent;
  font-size: 0.95rem;
  padding: 10px 18px;
  cursor: pointer;
  transition: background var(--transition-fast), color var(--transition-fast), box-shadow var(--transition-fast), transform var(--transition-fast), border-color var(--transition-fast);
}

.btn-full {
  width: 100%;
}

.btn-primary {
  background: linear-gradient(135deg, #f97316, #ea580c);
  color: #ffffff;
  box-shadow: 0 10px 25px rgba(249, 115, 22, 0.4);
}

.btn-primary:hover {
  transform: translateY(-1px);
  box-shadow: 0 18px 40px rgba(249, 115, 22, 0.5);
}

.btn-outline {
  background: transparent;
  color: var(--accent);
  border-color: var(--accent);
}

.btn-outline:hover {
  background: var(--accent-soft);
}

.btn-ghost {
  background: rgba(255, 255, 255, 0.0);
  border-color: transparent;
  color: var(--text-muted);
}

.btn-ghost:hover {
  background: rgba(255, 255, 255, 0.8);
}

.btn-text {
  background: transparent;
  border-color: transparent;
  color: var(--accent);
  padding-inline: 0;
}

.btn-text:hover {
  color: #c2410c;
}

/* Links */
a {
  color: var(--accent);
  text-decoration: none;
}

a:hover {
  text-decoration: underline;
}
