/* ======================================================================
 * usuarios.css • PBQE-C V2 Clean Light Premium – Módulo Usuários
 * ---------------------------------------------------------------------- */

/* Wrapper de autenticação */
.auth-wrapper {
  width: 100%;
  max-width: 420px;
}

.auth-card {
  background: rgba(255, 255, 255, 0.88);
  border-radius: 20px;
  padding: 24px 22px 20px;
  border: 1px solid rgba(148, 163, 184, 0.4);
  box-shadow: var(--shadow-soft);
  backdrop-filter: blur(14px);
}

.auth-header {
  margin-bottom: 18px;
}

.auth-title {
  font-size: 1.7rem;
}

.auth-subtitle {
  margin-top: 4px;
  font-size: 0.9rem;
  color: var(--text-muted);
}

/* Formulários */
.auth-form {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.inline-grid {
  display: grid;
  grid-template-columns: 1.2fr 0.9fr auto;
  gap: 10px 12px;
  align-items: end;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.form-group label {
  font-size: 0.85rem;
  color: var(--text-muted);
}

.form-group input {
  border-radius: 999px;
  border: 1px solid var(--border-soft);
  padding: 9px 13px;
  font-size: 0.9rem;
  outline: none;
  transition:
    border-color var(--transition-fast),
    box-shadow var(--transition-fast),
    background var(--transition-fast),
    transform var(--transition-fast);
  background: rgba(255, 255, 255, 0.9);
}

.form-group input:focus {
  border-color: var(--accent);
  box-shadow: 0 0 0 1px rgba(249, 115, 22, 0.35);
  background: #ffffff;
  transform: translateY(-0.5px);
}

/* Mensagens */
.auth-message {
  min-height: 22px;
  margin-top: 10px;
  font-size: 0.85rem;
  color: var(--text-muted);
}

.auth-message.is-error {
  color: var(--danger);
}

.auth-message.is-success {
  color: var(--success);
}

.auth-message.is-warn {
  color: var(--warn);
}

/* Rodapé */
.auth-footer {
  margin-top: 18px;
  font-size: 0.85rem;
  color: var(--text-muted);
}

/* Ações extras */
.auth-extra {
  margin-top: 12px;
  display: flex;
  justify-content: space-between;
  gap: 8px;
}

/* Topbar & dashboard */
.topbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px 14px;
  border-radius: 999px;
  background: rgba(255, 255, 255, 0.85);
  border: 1px solid rgba(148, 163, 184, 0.45);
  box-shadow: 0 18px 46px rgba(15, 23, 42, 0.2);
  backdrop-filter: blur(18px);
  margin-bottom: 18px;
}

.topbar-logo {
  font-weight: 700;
  letter-spacing: 0.04em;
}

.topbar-module {
  font-size: 0.9rem;
  color: var(--text-muted);
  margin-left: 8px;
}

.dashboard-main {
  max-width: 960px;
  margin: 0 auto;
  margin-top: 10px;
}

/* Cards e painéis */
.panel-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 14px;
}

.panel-card {
  background: rgba(255, 255, 255, 0.9);
  border-radius: 20px;
  padding: 18px 16px 16px;
  border: 1px solid rgba(148, 163, 184, 0.4);
  box-shadow: var(--shadow-soft);
  backdrop-filter: blur(14px);
}

.panel-card h2 {
  font-size: 1.05rem;
  margin-bottom: 4px;
}

.panel-card p {
  font-size: 0.88rem;
  color: var(--text-muted);
  margin-bottom: 10px;
}

.panel-card .btn {
  margin-top: 2px;
}

.panel-wide {
  margin-top: 18px;
}

.panel-text {
  font-size: 0.9rem;
  color: var(--text-muted);
  margin-bottom: 10px;
}

.panel-confirm {
  margin-top: 4px;
}

/* Tela de boas-vindas */
.welcome-wrapper {
  max-width: 480px;
  width: 100%;
}

.welcome-card {
  background: rgba(255, 255, 255, 0.92);
  border-radius: 22px;
  padding: 26px 20px 20px;
  border: 1px solid rgba(148, 163, 184, 0.42);
  box-shadow: var(--shadow-soft);
  backdrop-filter: blur(16px);
  text-align: center;
}

.welcome-card h1 {
  margin-bottom: 8px;
}

.welcome-card p {
  margin-top: 0;
  margin-bottom: 16px;
  font-size: 0.9rem;
  color: var(--text-muted);
}

/* Responsivo */
@media (max-width: 640px) {
  .auth-card {
    padding-inline: 18px;
  }

  .inline-grid {
    grid-template-columns: 1fr;
  }

  .topbar {
    border-radius: 16px;
  }
}
