/* =====================================================================
 * Painel Central ‚Ä¢ PBQE-C V2 ‚Äì Estilos espec√≠ficos
 * ===================================================================== */

.page-dashboard {
  background: radial-gradient(circle at top, rgba(255,255,255,0.9), #eef2f7 55%, #dde3f0);
  min-height: 100vh;
  display: flex;
  align-items: stretch;
  justify-content: center;
  padding: 2rem 1rem;
  box-sizing: border-box;
}

.dashboard-wrapper {
  width: 100%;
  max-width: 1080px;
  background: rgba(255, 255, 255, 0.92);
  border-radius: 18px;
  box-shadow:
    0 18px 45px rgba(15, 23, 42, 0.18),
    0 0 0 1px rgba(148, 163, 184, 0.25);
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Header */

.dashboard-header {
  padding: 1.25rem 1.75rem;
  border-bottom: 1px solid rgba(148, 163, 184, 0.35);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 1.5rem;
  backdrop-filter: blur(8px);
}

.brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.brand-mark {
  font-size: 1.8rem;
}

.brand-text {
  display: flex;
  flex-direction: column;
}

.brand-title {
  font-size: 1.35rem;
  margin: 0;
  color: #0f172a;
}

.brand-subtitle {
  font-size: 0.85rem;
  margin: 0;
  color: #64748b;
}

.user-info {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.9rem;
}

.user-label {
  color: #64748b;
}

.user-name {
  font-weight: 600;
  color: #0f172a;
}

/* Main layout */

.dashboard-main {
  padding: 1.75rem;
  display: flex;
  flex-direction: column;
  gap: 1.75rem;
}

.dashboard-section {
  background: rgba(248, 250, 252, 0.9);
  border-radius: 14px;
  padding: 1.5rem;
  border: 1px solid rgba(148, 163, 184, 0.25);
}

.section-header {
  margin-bottom: 1.25rem;
}

.section-title {
  margin: 0;
  font-size: 1.1rem;
  color: #0f172a;
}

.section-subtitle {
  margin: 0.25rem 0 0;
  font-size: 0.9rem;
  color: #64748b;
}

/* Cards */

.cards-grid {
  display: grid;
  gap: 1.25rem;
}

@media (min-width: 768px) {
  .cards-grid {
    grid-template-columns: repeat(3, minmax(0, 1fr));
  }
}

.dashboard-card {
  background: white;
  border-radius: 14px;
  padding: 1.1rem 1rem;
  border: 1px solid rgba(148, 163, 184, 0.3);
  box-shadow: 0 10px 25px rgba(15, 23, 42, 0.06);
  display: flex;
  flex-direction: column;
  gap: 0.55rem;
  position: relative;
}

.dashboard-card h3 {
  margin: 0;
  font-size: 1rem;
  color: #0f172a;
}

.dashboard-card p {
  margin: 0;
  font-size: 0.9rem;
  color: #64748b;
}

.dashboard-card .btn {
  margin-top: 0.5rem;
}

.dashboard-card.is-disabled {
  opacity: 0.6;
}

.dashboard-card.is-disabled .btn {
  pointer-events: none;
}

/* Tag em breve */

.tag {
  position: absolute;
  top: 0.75rem;
  right: 0.75rem;
  padding: 0.1rem 0.45rem;
  font-size: 0.7rem;
  border-radius: 999px;
  background: rgba(59, 130, 246, 0.1);
  color: #1d4ed8;
  border: 1px solid rgba(59, 130, 246, 0.4);
}

/* Status */

.status-box {
  padding: 0.75rem 1rem;
  border-radius: 10px;
  background: #ecfdf5;
  border: 1px solid #4ade80;
  font-size: 0.9rem;
  color: #166534;
}

/* Bot√£o pequeno, reaproveitando classe base */

.btn-sm {
  padding: 0.25rem 0.7rem;
  font-size: 0.78rem;
}

// ======================================================================
// üßô‚Äç‚ôÇÔ∏è painel.js ‚Ä¢ Painel Central PBQE-C V2
// ----------------------------------------------------------------------
// Vers√£o inicial: foco em navega√ß√£o e prepara√ß√£o para leitura do usu√°rio
// logado no futuro (via sess√£o, token ou endpoint dedicado).
// ======================================================================

document.addEventListener('DOMContentLoaded', () => {
  const userEl = document.getElementById('dashboardUser');
  const statusEl = document.getElementById('dashboardStatus');
  const btnLogout = document.getElementById('btnLogout');

  // Placeholder: no futuro vamos ler o usu√°rio logado de um endpoint
  // (/api/usuarios/me) ou de um token salvo com seguran√ßa.
  try {
    const storedUser = sessionStorage.getItem('usuarioAtual') || localStorage.getItem('usuarioAtual');
    if (storedUser) {
      userEl.textContent = storedUser;
    } else {
      userEl.textContent = 'Usu√°rio autenticado';
    }
  } catch (e) {
    console.warn('N√£o foi poss√≠vel ler usu√°rio do storage:', e);
    userEl.textContent = 'Usu√°rio autenticado';
  }

  if (statusEl) {
    statusEl.textContent = 'Painel carregado com sucesso. M√≥dulo Usu√°rios V2 ativo.';
  }

  // Navega√ß√£o dos cards
  document.querySelectorAll('[data-href]').forEach((btn) => {
    btn.addEventListener('click', () => {
      const href = btn.getAttribute('data-href');
      if (href) {
        window.location.href = href;
      }
    });
  });

  // Logout b√°sico (placeholder)
  if (btnLogout) {
    btnLogout.addEventListener('click', () => {
      try {
        sessionStorage.removeItem('usuarioAtual');
        localStorage.removeItem('usuarioAtual');
      } catch (e) {
        console.warn('Erro ao limpar storage no logout:', e);
      }
      window.location.href = '/modules/usuarios/login.html';
    });
  }
});
