document.addEventListener('DOMContentLoaded', () => {
  const storedUser = sessionStorage.getItem('usuarioAtual') || localStorage.getItem('usuarioAtual');

  if (!storedUser) {
    return window.location.href = '/modules/login/login.html';
  }

  const userEl = document.getElementById('dashboardUser');
  if (userEl) userEl.textContent = storedUser;

  const statusEl = document.getElementById('dashboardStatus');
  if (statusEl) statusEl.textContent = 'Painel carregado com sucesso. SeguranÃ§a PBQE-C ativa.';

  document.querySelectorAll('[data-href]').forEach(btn => {
    btn.addEventListener('click', () => {
      let destino = btn.getAttribute('data-href');
      window.location.href = destino;
    });
  });

  document.getElementById('btnLogout')?.addEventListener('click', () => {
    sessionStorage.removeItem('usuarioAtual');
    localStorage.removeItem('usuarioAtual');
    window.location.href = '/modules/login/login.html';
  });
});
