:root{--bg-body:#f3f4f6;--bg-card:#fff;--border-soft:#e5e7eb;--text-main:#111827;--text-muted:#6b7280;--accent:#f97316;--accent-soft:rgba(249,115,22,.08);--danger:#dc2626;--success:#16a34a;--warn:#d97706;--radius-lg:16px;--shadow-soft:0 18px 45px rgba(15,23,42,.12);--transition-fast:150ms ease-out}*,*::before,*::after{box-sizing:border-box}body{margin:0;font-family:system-ui,-apple-system,BlinkMacSystemFont,'Segoe UI',sans-serif;background:radial-gradient(circle at top,#fef3c7 0,#f3f4f6 42%,#e5e7eb 100%);color:var(--text-main)}.page{min-height:100vh}.page-auth,.page-welcome{display:flex;align-items:center;justify-content:center;padding:24px}.page-dashboard{padding:16px}h1,h2,h3,h4{margin:0;font-weight:600}.btn{display:inline-flex;align-items:center;justify-content:center;gap:6px;border-radius:999px;border:1px solid transparent;font-size:.95rem;padding:10px 18px;cursor:pointer;transition:background var(--transition-fast),color var(--transition-fast),box-shadow var(--transition-fast),transform var(--transition-fast),border-color var(--transition-fast)}.btn-full{width:100%}.btn-primary{background:linear-gradient(135deg,#f97316,#ea580c);color:#fff;box-shadow:0 10px 25px rgba(249,115,22,.4)}.btn-primary:hover{transform:translateY(-1px);box-shadow:0 18px 40px rgba(249,115,22,.5)}.btn-text{background:transparent;border-color:transparent;color:var(--accent)}.btn-text:hover{color:#c2410c}a{color:var(--accent);text-decoration:none}a:hover{text-decoration:underline}

/* === PBQE-C | Ajustes Visuais Painel v1 === */
.page-dashboard {
  background: linear-gradient(180deg, #f8fafc 0%, #eef2f7 100%);
}

/* =====================================================================
 * Painel Central • PBQE-C V2 – Estilos específicos
 * ===================================================================== */

.page-dashboard {
  background: radial-gradient(circle at top, rgba(255,255,255,0.9), #eef2f7 55%, #dde3f0);
  min-height: 100vh;
  display: flex;
  align-items: stretch;
  justify-content: center;
  padding: 2rem 1rem;
  box-sizing: border-box;
}

.dashboard-wrapper {
  width: 100%;
  max-width: 1080px;
  background: rgba(255, 255, 255, 0.92);
  border-radius: 18px;
  box-shadow:
    0 18px 45px rgba(15, 23, 42, 0.18),
    0 0 0 1px rgba(148, 163, 184, 0.25);
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Header */

.dashboard-header {
  padding: 1.25rem 1.75rem;
  border-bottom: 1px solid rgba(148, 163, 184, 0.35);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 1.5rem;
  backdrop-filter: blur(8px);
}

.brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.brand-mark {
  font-size: 1.8rem;
}

.brand-text {
  display: flex;
  flex-direction: column;
}

.brand-title {
  font-size: 1.35rem;
  margin: 0;
  color: #0f172a;
}

.brand-subtitle {
  font-size: 0.85rem;
  margin: 0;
  color: #64748b;
}

.user-info {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.9rem;
}

.user-label {
  color: #64748b;
}

.user-name {
  font-weight: 600;
  color: #0f172a;
}

/* Main layout */

.dashboard-main {
  padding: 1.75rem;
  display: flex;
  flex-direction: column;
  gap: 1.75rem;
}

.dashboard-section {
  background: rgba(248, 250, 252, 0.9);
  border-radius: 14px;
  padding: 1.5rem;
  border: 1px solid rgba(148, 163, 184, 0.25);
}

.section-header {
  margin-bottom: 1.25rem;
}

.section-title {
  margin: 0;
  font-size: 1.1rem;
  color: #0f172a;
}

.section-subtitle {
  margin: 0.25rem 0 0;
  font-size: 0.9rem;
  color: #64748b;
}

/* Cards */

.cards-grid {
  display: grid;
  gap: 1.25rem;
}

@media (min-width: 768px) {
  .cards-grid {
    grid-template-columns: repeat(3, minmax(0, 1fr));
  }
}

.dashboard-card {
  background: white;
  border-radius: 14px;
  padding: 1.1rem 1rem;
  border: 1px solid rgba(148, 163, 184, 0.3);
  box-shadow: 0 10px 25px rgba(15, 23, 42, 0.06);
  display: flex;
  flex-direction: column;
  gap: 0.55rem;
  position: relative;
}

.dashboard-card h3 {
  margin: 0;
  font-size: 1rem;
  color: #0f172a;
}

.dashboard-card p {
  margin: 0;
  font-size: 0.9rem;
  color: #64748b;
}

.dashboard-card .btn {
  margin-top: 0.5rem;
}

.dashboard-card.is-disabled {
  opacity: 0.6;
}

.dashboard-card.is-disabled .btn {
  pointer-events: none;
}

/* Tag em breve */

.tag {
  position: absolute;
  top: 0.75rem;
  right: 0.75rem;
  padding: 0.1rem 0.45rem;
  font-size: 0.7rem;
  border-radius: 999px;
  background: rgba(59, 130, 246, 0.1);
  color: #1d4ed8;
  border: 1px solid rgba(59, 130, 246, 0.4);
}

/* Status */

.status-box {
  padding: 0.75rem 1rem;
  border-radius: 10px;
  background: #ecfdf5;
  border: 1px solid #4ade80;
  font-size: 0.9rem;
  color: #166534;
}

/* Botão pequeno, reaproveitando classe base */

.btn-sm {
  padding: 0.25rem 0.7rem;
  font-size: 0.78rem;
}


/* === PBQE-C | Ajustes Visuais Painel v1 === */
.dashboard-header {
  padding: 0.9rem 1.5rem;
}

.dashboard-card {
  padding: 0.9rem 0.95rem;
  gap: 0.4rem;
}

.btn-secondary {
  background: #f1f5f9;
  color: #0f172a;
  border: 1px solid #cbd5f5;
}

.btn-secondary:hover {
  background: #e2e8f0;
}

.tag {
  font-size: 0.72rem;
  padding: 0.15rem 0.55rem;
  font-weight: 600;
}

.status-box {
  padding: 0.55rem 0.9rem;
  font-size: 0.85rem;
}
