const { execSync } = require('child_process');
const { Client } = require('pg');
const fs = require('fs');
const path = require('path');

const OUTPUT_FILE = path.join(
  'C:\\Users\\Claudio\\Desktop\\petropolitan\\sistema_Petropolian\\Petropolitan_Lab',
  'snapshot_petropolitan_lab.txt'
);

fs.writeFileSync(OUTPUT_FILE, '=== SNAPSHOT PETROPOLITAN LAB ===\\n\\n');

function log(title, content) {
  fs.appendFileSync(OUTPUT_FILE, `\\n=== ${title} ===\\n`);
  fs.appendFileSync(OUTPUT_FILE, content + '\\n');
}

// Script mora em backend/scripts
// Alvos estão em backend/modules e backend/public
const modulesPath = path.join(process.cwd(), '..', 'modules');
const publicPath  = path.join(process.cwd(), '..', 'public');

try {
  const treeModules = execSync(`tree /A /F \"${modulesPath}\"`, { encoding: 'utf8' });
  log('TREE backend/modules', treeModules);
} catch (err) {
  log('ERRO TREE backend/modules', err.message);
}

try {
  const treePublic = execSync(`tree /A /F \"${publicPath}\"`, { encoding: 'utf8' });
  log('TREE backend/public', treePublic);
} catch (err) {
  log('ERRO TREE backend/public', err.message);
}

(async () => {
  const client = new Client({
    user: 'bruxao',
    host: 'localhost',
    database: 'petropolitan_lab',
    password: 'bruxao123',
    port: 5432
  });

  try {
    await client.connect();

    const res = await client.query(`
      SELECT table_schema, table_name
      FROM information_schema.tables
      WHERE table_type = 'BASE TABLE'
        AND table_schema NOT IN ('pg_catalog', 'information_schema')
      ORDER BY table_schema, table_name;
    `);

    const tables = res.rows
      .map(t => `${t.table_schema}.${t.table_name}`)
      .join('\\n');

    log('TABELAS DO BANCO petropolitan_lab', tables);

  } catch (err) {
    log('ERRO BANCO', err.message);
  } finally {
    await client.end();
  }
})();
